    private void label_registrar_adminMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_label_registrar_adminMouseClicked
        String user = input_admin_user.getText();
        char[] pass = input_admin_pass.getPassword();
        String nombre = input_admin_nombre.getText();
        String apellido = input_admin_apellido.getText();
        administrador admin = new administrador(user, pass, nombre, apellido);
        JOptionPane.showMessageDialog(null, "El user name es: "+admin.getUser() + " su nombre es: "+admin.getNombre()+ " "+ admin.getApellido(), "datos admin", -1);
    }//GEN-LAST:event_label_registrar_adminMouseClicked

